#ifndef ATOMICAGGREGATE_H
#define ATOMICAGGREGATE_H

#include "attribute.h"
#include <string>
#include <typeinfo>

using namespace std;  

/**
 * The atomic aggregate path attribute.  It is used to inform other BGP
 * speakers that the local system selected a less specific route without
 * selecting a more specific route which is included in it.  It is well-known
 * and discretionary.
 */
class AtomicAggregate : public Attribute  {
public: 
	AtomicAggregate();
  AtomicAggregate(char * stream);
	virtual ~AtomicAggregate();
  bool equals(Attribute *attrib);
  int bytecount();
  Attribute * copy();
  string toString();
  void toStream(char * stream);
  
public:
  // ......................... constants ........................... //
  /** The atomic aggregate path attribute type code. */
  const static int TYPECODE = 6;
};

#endif
